interface OrbDefinition {
  name: string;
  traits: string[];
  geometry?: string;
  position?: [number, number, number];
  scale?: number | [number, number, number];
  color?: string;
}

function parseOrb(content: string): OrbDefinition | null {
  const orbMatch = content.match(/orb\s+["']?([\w_]+)["']?\s*\{([^}]*)\}/s);
  if (!orbMatch) return null;

  const name = orbMatch[1];
  const body = orbMatch[2];

  // Extract traits
  const traits = [...body.matchAll(/@(\w+)/g)].map(m => m[1]);

  // Extract geometry
  const geoMatch = body.match(/geometry:\s*["']([\w]+)["']/);
  const geometry = geoMatch?.[1];

  // Extract position
  const posMatch = body.match(/position:\s*\[([^\]]+)\]/);
  const position = posMatch 
    ? posMatch[1].split(',').map(n => parseFloat(n.trim())) as [number, number, number]
    : undefined;

  return { name, traits, geometry, position };
}

