import * as THREE from 'three';

interface HoloObject {
  name: string;
  geometry: string;
  position: [number, number, number];
  scale: number | [number, number, number];
  color: string;
}

function createThreeGeometry(type: string): THREE.BufferGeometry {
  switch (type) {
    case 'sphere':
    case 'orb':
      return new THREE.SphereGeometry(0.5, 32, 32);
    case 'cube':
      return new THREE.BoxGeometry(1, 1, 1);
    case 'cylinder':
      return new THREE.CylinderGeometry(0.5, 0.5, 1, 32);
    case 'cone':
      return new THREE.ConeGeometry(0.5, 1, 32);
    case 'torus':
      return new THREE.TorusGeometry(0.4, 0.15, 16, 48);
    case 'plane':
      return new THREE.PlaneGeometry(1, 1);
    default:
      return new THREE.BoxGeometry(1, 1, 1);
  }
}

export function holoToThreeMesh(obj: HoloObject): THREE.Mesh {
  const geometry = createThreeGeometry(obj.geometry);
  const material = new THREE.MeshStandardMaterial({
    color: obj.color,
    roughness: 0.4,
    metalness: 0.1
  });

  const mesh = new THREE.Mesh(geometry, material);
  mesh.position.set(...obj.position);

  const scale = Array.isArray(obj.scale) 
    ? obj.scale 
    : [obj.scale, obj.scale, obj.scale];
  mesh.scale.set(...scale);
  mesh.name = obj.name;

  return mesh;
}

