import React, { useState } from 'react';

interface SceneNode {
  type: 'object' | 'template' | 'spatial_group';
  name: string;
  children?: SceneNode[];
  traits?: string[];
}

interface SceneTreeProps {
  nodes: SceneNode[];
  onSelect: (node: SceneNode) => void;
}

export const SceneTree: React.FC<SceneTreeProps> = ({ nodes, onSelect }) => {
  const [expanded, setExpanded] = useState<Set<string>>(new Set());

  const toggle = (name: string) => {
    const next = new Set(expanded);
    if (next.has(name)) next.delete(name);
    else next.add(name);
    setExpanded(next);
  };

  const renderNode = (node: SceneNode, depth = 0) => (
    <div key={node.name} style={{ marginLeft: depth * 16 }}>
      <div className="node-row" onClick={() => onSelect(node)}>
        {node.children && (
          <span onClick={(e) => { e.stopPropagation(); toggle(node.name); }}>
            {expanded.has(node.name) ? 'â–¼' : 'â–¶'}
          </span>
        )}
        <span className={`icon-${node.type}`} />
        <span>{node.name}</span>
        {node.traits?.map(t => <span key={t} className="trait">@{t}</span>)}
      </div>
      {expanded.has(node.name) && node.children?.map(c => renderNode(c, depth + 1))}
    </div>
  );

  return <div className="scene-tree">{nodes.map(n => renderNode(n))}</div>;
};

const VALID_TRAITS = new Set([
  // Interaction
  'grabbable', 'throwable', 'holdable', 'clickable', 'hoverable',
  'draggable', 'pointable', 'scalable',
  // Physics
  'collidable', 'physics', 'rigid', 'kinematic', 'trigger', 'gravity',
  // Visual
  'glowing', 'emissive', 'transparent', 'reflective', 'animated', 'billboard',
  // Networking
  'networked', 'synced', 'persistent', 'owned', 'host_only',
  // Behavior
  'stackable', 'attachable', 'equippable', 'consumable', 'destructible',
  // Spatial
  'anchor', 'tracked', 'world_locked', 'hand_tracked', 'eye_tracked',
  // Audio
  'spatial_audio', 'ambient', 'voice_activated',
  // State
  'state', 'reactive', 'observable', 'computed'
]);

interface TraitValidationResult {
  valid: boolean;
  unknownTraits: string[];
  suggestions: Map<string, string>;
}

export function validateTraits(traits: string[]): TraitValidationResult {
  const unknown: string[] = [];
  const suggestions = new Map<string, string>();

  for (const trait of traits) {
    if (!VALID_TRAITS.has(trait)) {
      unknown.push(trait);
      // Find closest match
      let best = '';
      let bestScore = 0;
      for (const valid of VALID_TRAITS) {
        const score = similarity(trait, valid);
        if (score > bestScore) {
          bestScore = score;
          best = valid;
        }
      }
      if (bestScore > 0.5) suggestions.set(trait, best);
    }
  }

  return { valid: unknown.length === 0, unknownTraits: unknown, suggestions };
}

function similarity(a: string, b: string): number {
  const longer = a.length > b.length ? a : b;
  const shorter = a.length > b.length ? b : a;
  if (longer.length === 0) return 1.0;
  return (longer.length - editDistance(longer, shorter)) / longer.length;
}

