export function parseHoloFile(content: string): HoloComposition {
  const nameMatch = content.match(/composition\s+"([^"]+)"/);
  const name = nameMatch?.[1] ?? 'Untitled';

  const envMatch = content.match(/environment\s*\{([^}]+)\}/s);
  const environment = envMatch ? parseEnvironment(envMatch[1]) : undefined;

  const templates = new Map<string, HoloTemplate>();
  const templateRegex = /template\s+"([^"]+)"\s*\{([^}]+)\}/gs;
  let tMatch;
  while ((tMatch = templateRegex.exec(content))) {
    templates.set(tMatch[1], parseTemplate(tMatch[1], tMatch[2]));
  }

  return { name, environment, templates, objects: [], spatialGroups: [] };
}

import React, { useState } from 'react';

const TRAIT_CATEGORIES = {
  interaction: ['grabbable', 'throwable', 'holdable', 'clickable', 'hoverable', 'draggable', 'pointable', 'scalable'],
  physics: ['collidable', 'physics', 'rigid', 'kinematic', 'trigger', 'gravity'],
  visual: ['glowing', 'emissive', 'transparent', 'reflective', 'animated', 'billboard'],
  networking: ['networked', 'synced', 'persistent', 'owned', 'host_only'],
  behavior: ['stackable', 'attachable', 'equippable', 'consumable', 'destructible'],
  spatial: ['anchor', 'tracked', 'world_locked', 'hand_tracked', 'eye_tracked'],
  audio: ['spatial_audio', 'ambient', 'voice_activated'],
  state: ['state', 'reactive', 'observable', 'computed']
};

interface TraitPickerProps {
  selected: string[];
  onChange: (traits: string[]) => void;
}

export const TraitPicker: React.FC<TraitPickerProps> = ({ selected, onChange }) => {
  const [expandedCat, setExpandedCat] = useState<string | null>('interaction');

  const toggle = (trait: string) => {
    if (selected.includes(trait)) {
      onChange(selected.filter(t => t !== trait));
    } else {
      onChange([...selected, trait]);
    }
  };

  return (
    <div className="trait-picker">
      {Object.entries(TRAIT_CATEGORIES).map(([cat, traits]) => (
        <div key={cat} className="category">
          <h4 onClick={() => setExpandedCat(expandedCat === cat ? null : cat)}>
            {expandedCat === cat ? 'â–¼' : 'â–¶'} {cat}
          </h4>
          {expandedCat === cat && (
            <div className="traits">
              {traits.map(t => (
                <label key={t}>
                  <input
                    type="checkbox"
                    checked={selected.includes(t)}
                    onChange={() => toggle(t)}
                  />
                  @{t}
                </label>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};
