type GeometryType = 'sphere' | 'cube' | 'cylinder' | 'cone' | 'torus' | 'plane';

const VALID_GEOMETRIES: GeometryType[] = ['sphere', 'cube', 'cylinder', 'cone', 'torus', 'plane'];

function parseGeometry(content: string): GeometryType {
  const match = content.match(/geometry:\s*["']([\w]+)["']/);
  if (match && VALID_GEOMETRIES.includes(match[1] as GeometryType)) {
    return match[1] as GeometryType;
  }
  return 'cube'; // default
}

function parseColor(content: string): number {
  const match = content.match(/color:\s*["']#?([\da-fA-F]{6})["']/);
  if (match) {
    return parseInt(match[1], 16);
  }
  return 0x4a9eff; // default blue
}

function parsePosition(content: string): [number, number, number] {
  const match = content.match(/position:\s*\[([^\]]+)\]/);
  if (match) {
    const nums = match[1].split(',').map(n => parseFloat(n.trim()) || 0);
    return [nums[0] ?? 0, nums[1] ?? 0, nums[2] ?? 0];
  }
  return [0, 0, 0];
}

import React from 'react';

interface ObjectProps {
  name: string;
  geometry: string;
  position: [number, number, number];
  color: string;
  traits: string[];
}

export const ObjectCard: React.FC<ObjectProps> = ({ name, geometry, position, color, traits }) => (
  <div className="object-card">
    <h3>{name}</h3>
    <div className="geometry">{geometry}</div>
    <div className="position">[{position.join(', ')}]</div>
    <div className="color" style={{ backgroundColor: color }} />
    <div className="traits">
      {traits.map(t => <span key={t}>@{t}</span>)}
    </div>
  </div>
);
