script:
object TurretBase {
  geometry: 'cylinder'
  size: [1, 0.5, 1]
  color: '#444'
  physics: { type: 'static' }
}

object TurretHead @pointable {
  geometry: 'sphere'
  position: [0, 0.5, 0]
  color: '#666'

  animation scan {
    property: 'rotation.y'
    from: -45
    to: 45
    duration: 2000
    loop: true
    easing: 'easeInOut'
  }

  onPoint: {
    this.color = '#ff0000'
    audio.play('target_locked')
  }
}
