craftingSystem("blacksmithing", {
  name: "Blacksmithing",
  description: "Forge weapons and armor from raw materials",

  requirements: {
    station: "forge",
    tools: ["hammer", "tongs"]
  },

  skillProgression: {
    maxLevel: 100,
    xpPerCraft: (recipe, quality) => recipe.baseXP * quality.xpMultiplier,

    tiers: [
      { level: 1, name: "Apprentice", recipes: ["iron_*"] },
      { level: 25, name: "Journeyman", recipes: ["steel_*"] },
      { level: 50, name: "Expert", recipes: ["mithril_*"] },
      { level: 75, name: "Master", recipes: ["dragon_*"] },
      { level: 100, name: "Grandmaster", recipes: ["legendary_*"] }
    ]
  },

  qualityTiers: [
    {
      name: "Poor",
      chance: 0.10,
      statMultiplier: 0.8,
      xpMultiplier: 0.5,
      color: "#808080",
      skillModifier: -20
    },
    {
      name: "Common",
      chance: 0.50,
      statMultiplier: 1.0,
      xpMultiplier: 1.0,
      color: "#ffffff",
      skillModifier: 0
    },
    {
      name: "Fine",
      chance: 0.25,
      statMultiplier: 1.15,
      xpMultiplier: 1.25,
      color: "#00ff00",
      skillModifier: 10
    },
    {
      name: "Superior",
      chance: 0.10,
      statMultiplier: 1.3,
      xpMultiplier: 1.5,
      color: "#0088ff",
      skillModifier: 25
    },
    {
      name: "Exceptional",
      chance: 0.04,
      statMultiplier: 1.5,
      xpMultiplier: 2.0,
      color: "#aa00ff",
      skillModifier: 40
    },
    {
      name: "Masterwork",
      chance: 0.01,
      statMultiplier: 2.0,
      xpMultiplier: 3.0,
      color: "#ffaa00",
      skillModifier: 60,
      requiresLevel: 75
    }
  ],

  calculateQuality: (recipe, playerSkill) => {
    const skillDiff = playerSkill - recipe.requiredLevel;
    const bonusChance = Math.min(skillDiff * 0.01, 0.3);

    let roll = Math.random();
    let cumulative = 0;

    for (const tier of this.qualityTiers.reverse()) {
      if (tier.requiresLevel && playerSkill < tier.requiresLevel) continue;

      let adjustedChance = tier.chance + (tier.skillModifier > 0 ? bonusChance * (tier.skillModifier / 60) : 0);
      cumulative += adjustedChance;

      if (roll <= cumulative) return tier;
    }

    return this.qualityTiers.find(t => t.name === "Common");
  }
})

